package utils_test

import (
	"encoding/hex"
	"testing"

	"github.com/scalarorg/protocol-signer/utils"
)

func TestNewBtcTxFromBase64(t *testing.T) {
	psbt := ""

	btcTx, txBytes, err := utils.NewBTCTxFromHex(psbt)
	if err != nil {
		t.Error(err)
	}
	txHex := hex.EncodeToString(txBytes)
	t.Log(btcTx)
	t.Log(txBytes)
	t.Log(txHex)
}
